			  $.each($('.ratings-result'), function () {
				  var $this = $(this),
					  parentWidth = $this.closest('.ratings').width(),
					  rating = $(this).data('result'),
					  newWidth = (parentWidth / 100) * rating;
					  
				  $(this).css('width', newWidth);
			  });
