		  scrollToAnimation: function (speed, offset, e) {
			  /* General scroll to function */
			  var targetEl = $(this).attr('href'),
				  toTop = false;

			  if (!$(targetEl).length) {
				  if (targetEl === '#header' || targetEl === '#top' || targetEl === '#wrapper') {
					  targetPos = 0;
					  toTop = true;
				  } else {
					  return;
				  }
			  } else {
				  var elem = $(targetEl),
					  targetPos = offset ? ( elem.offset().top + offset ) : elem.offset().top;
			  }
			  
			  if (targetEl || toTop) {
				  $('html, body').animate({
			      'scrollTop': targetPos
			  }, speed || 1200);
			  e.preventDefault();
			  }
		  },
